package net.bitzoo.rosalind

object ReverseComplement extends App {

  private val dnaString1 = "AAAACCCGGT"
  private val dnaString2 = ""
  private val dnaReverseComplement = dnaString2.reverse.map(x =>
    if (x == 'C') 'G'
    else if (x == 'G') 'C'
    else if (x == 'A') 'T'
    else 'A')

  Console.println(dnaReverseComplement)

}